import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import Button from '@mui/material/Button';

const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
const [postIdToDelete, setPostIdToDelete] = useState('');

const handleOpenDeleteDialog = (postId: string) => {
  setPostIdToDelete(postId);
  setOpenDeleteDialog(true);
};

const handleCloseDeleteDialog = () => {
  setOpenDeleteDialog(false);
  setPostIdToDelete('');
};


interface Post {
  _id: string;
  title: string;
  body: string;
  // Other fields
}

function PostPage() {
  const { postId } = useParams<{ postId: string }>();
  const [post, setPost] = useState<Post | null>(null);

  useEffect(() => {
    axios.get(`/api/posts/${postId}`)
      .then(response => {
        setPost(response.data);
      })
      .catch(error => {
        console.error('Error fetching blog post:', error);
      });
  }, [postId]);

  const handleRemovePost = () => {
    if (window.confirm('Are you sure you want to remove this post?')) {
      // Send a DELETE request to remove the post
      axios.delete(`/api/deletePost/${postId}`)
        .then(() => {
          // Redirect to a different page after post removal
          history.push('/api/posts/${category._id}/${subCategory._id}'); // Change to the appropriate URL
        })
        .catch(error => {
          console.error('Error removing blog post:', error);
        });
    }
  };

  return (
    <div>
      {post ? (
        <div>
          <h2>{post.title}</h2>
          <p>{post.body}</p>
          {/* Display other post details */}
          <button onClick={handleRemovePost}>Remove</button>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default PostPage;
