import React, { useState } from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import { FormControl, InputLabel, Select, MenuItem } from "@mui/material";

const handleSubmit = (event: any) => {
  event.preventDefault();
  // Here you can handle form submission or API calls with the form data
  console.log(FormData);
};

function ManagePost() {
  const [selectedCategory, setSelectedCategory] = useState("");
  const [selectedSubcategory, setSelectedSubcategory] = useState("");

  const handleCategoryChange = (event: any) => {
    setSelectedCategory(event.target.value);
    setSelectedSubcategory("");
  };

  const handleSubcategoryChange = (event: any) => {
    setSelectedSubcategory(event.target.value);
  };

  const initialCategoriesList = [
    {
      name: "Technology",
      subcategories: ["Programming", "Hardware", "Software"],
    },
    {
      name: "Travel",
      subcategories: ["Adventure", "Cultural", "Nature"],
    },
    {
      name: "Food",
      subcategories: ["Recipes", "Restaurants", "Cooking Tips"],
    },
  ];

  return (
    <>
      <form onSubmit={handleSubmit}>
        <Box
          sx={{
            width: 500,
            maxWidth: "100%",
          }}
        >
          <TextField fullWidth label="Title" id="fullWidth" />
        </Box>

        <Box sx={{ width: 500, maxWidth: "100%" }}>
          <TextField
            multiline
            maxRows={12}
            variant="standard"
            fullWidth
            label="body"
            id="fullWidth"
          />
        </Box>

        <FormControl fullWidth>
          <InputLabel id="category-label">Category</InputLabel>
          <Select
            labelId="category-label"
            id="category-select"
            value={selectedCategory}
            onChange={handleCategoryChange}
            label="Category"
          >
            {initialCategoriesList.map((category) => (
              <MenuItem key={category.name} value={category.name}>
                {category.name}
              </MenuItem>
            ))}
          </Select>

          {selectedCategory && (
            <FormControl fullWidth>
              <InputLabel id="subcategory-label">Subcategory</InputLabel>
              <Select
                labelId="subcategory-label"
                id="subcategory-select"
                value={selectedSubcategory}
                onChange={handleSubcategoryChange}
                label="Subcategory"
              >

                {initialCategoriesList
                  .find((category) => category.name === selectedCategory)
                  ?.subcategories?.map((subcategory) => (
                    <MenuItem key={subcategory} value={subcategory}>
                      {subcategory}
                    </MenuItem>
                  ))}
                  
              </Select>
            </FormControl>
          )}
        </FormControl>

        <Button
          variant="contained"
        >
          Post
        </Button>
      </form>
    </>
  );
}

export default ManagePost;
