import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function SubcategoryPage() {
  const { categoryName, subCategoryName } = useParams<{ categoryName: string; subCategoryName: string }>();
  const [posts, setPosts] = useState<{ _id: string; title: string }[]>([]);
  const [category, setCategory] = useState<any>(null)
  const [subCategory, setSubCategory] = useState<any>(null)

  const getCategory = async () => {
    try {
      const res = await axios.get(`/api/categories/${categoryName}`);
      console.log(res.data);
      setCategory(res.data);
      const findSubCategory = res.data.subCategories.find
      setSubCategory();
    } catch (error) {
      navigate("/");
    }
  };

  useEffect(() => {
    async function fetchPosts() {
      try {
        const response = await axios.get(`/api/posts/${categoryName}/${subCategoryName}`);
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    }

    fetchPosts();
  }, [categoryName, subCategoryName]);

  return (
    <div>
      <h2>Posts in {subCategoryName}</h2>
      <ul>
        {posts.map((post) => (
          <li key={post._id}>{post.title}</li>
        ))}
      </ul>
    </div>
  );
}

export default SubcategoryPage;
