import React, { useState } from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import { FormControl, InputLabel, Select, MenuItem, SelectChangeEvent } from "@mui/material";

interface Category {
  name: string;
  subcategories: string[];
}

interface FormData {
  Title: string;
  body: string;
  Category: string;
  Subcategory: string;
}

const ManagePost: React.FC = () => {
  const [selectedCategory, setSelectedCategory] = useState("");
  const [selectedSubcategory, setSelectedSubcategory] = useState("");
  const [formData, setFormData] = useState<FormData>({
    Title: "",
    body: "",
    Category: "",
    Subcategory: "",
  });

  const handleCategoryChange = (event: SelectChangeEvent<string>) => {
    const category = event.target.value;
    setSelectedCategory(category);
    setSelectedSubcategory("");
    setFormData((prevData) => ({ ...prevData, Category: category, Subcategory: "" }));
  };

  const handleSubcategoryChange = (event: SelectChangeEvent<string>) => {
    const subcategory = event.target.value;
    setSelectedSubcategory(subcategory);
    setFormData((prevData) => ({ ...prevData, Subcategory: subcategory }));
  };

  const initialCategoriesList: Category[] = [
    {
      name: "Technology",
      subcategories: ["Programming", "Hardware", "Software"],
    },
    {
      name: "Travel",
      subcategories: ["Adventure", "Cultural", "Nature"],
    },
    {
      name: "Food",
      subcategories: ["Recipes", "Restaurants", "Cooking Tips"],
    },
  ];

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (event: React.FormEvent) => {
    event.preventDefault();
    // Handle form submission or API calls with the form data
    console.log(formData);
    console.log("working");
  };

  return (
    <form onSubmit={handleSubmit}>
      <Box sx={{ width: 500, maxWidth: "100%" }}>
        <TextField
          fullWidth
          label="Title"
          id="fullWidth"
          name="Title"
          value={formData.Title}
          onChange={handleChange}
        />
      </Box>

      <Box sx={{ width: 500, maxWidth: "100%" }}>
        <TextField
          multiline
          maxRows={12}
          variant="standard"
          fullWidth
          label="Body"
          id="fullWidth"
          name="body"
          value={formData.body}
          onChange={handleChange}
        />
      </Box>

      <FormControl fullWidth>
        <InputLabel id="category-label">Category</InputLabel>
        <Select
          labelId="category-label"
          id="category-select"
          value={selectedCategory}
          onChange={handleCategoryChange}
          label="Category"
          name="Category"
        >
          {initialCategoriesList.map((category) => (
            <MenuItem key={category.name} value={category.name}>
              {category.name}
            </MenuItem>
          ))}
        </Select>

        {selectedCategory && (
          <FormControl fullWidth>
            <InputLabel id="subcategory-label">Subcategory</InputLabel>
            <Select
              labelId="subcategory-label"
              id="subcategory-select"
              value={selectedSubcategory}
              onChange={handleSubcategoryChange}
              label="Subcategory"
              name="Subcategory"
            >
              {initialCategoriesList
                .find((category) => category.name === selectedCategory)
                ?.subcategories?.map((subcategory) => (
                  <MenuItem key={subcategory} value={subcategory}>
                    {subcategory}
                  </MenuItem>
                ))}
            </Select>
          </FormControl>
        )}
      </FormControl>

      <Button type="submit" variant="contained">
        Post
      </Button>
    </form>
  );
};

export default ManagePost;
